import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Card,
  CardBody,
  CardTitle,
  CardSubtitle,
} from "reactstrap";

export default class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: null,
    };

    this._isMounted = false;
  }

  componentDidMount() {
    this._isMounted = true;
    this._isMounted && this.getProduct();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  getProduct() {
    axios.get(process.env.REACT_APP_API_URL + "/product/all").then((res) => {
      console.log(res.data);
      this._isMounted &&
        this.setState({
          products: res.data,
        });
    });
  }

  genSuggProducts = () => {
    const { suggProducts } = this.state;

    return (
      suggProducts &&
      suggProducts.map((product) => {
        return (
          <Card key={product.product_id}>
            <div
              style={{
                width: "100%",
                height: "250px",
                backgroundImage: "url(" + product.product_image + ")",
                backgroundSize: "100%",
                backgroundRepeat: "no-repeat",
                backgroundColor: "#fff",
              }}
            ></div>

            <CardBody>
              <CardTitle>{product.product_name}</CardTitle>
              <CardSubtitle style={{ color: "#d93731" }}>
                <b>
                  - {product.product_price} บาท / {product.product_unit}
                </b>
              </CardSubtitle>
            </CardBody>
          </Card>
        );
      })
    );
  };

  render() {
    return <div>

    </div>;
  }
}
