import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Container, Row, Col } from "reactstrap";

import { ReactComponent as Line_SVG } from "../../assets/icon/line.svg";
import { ReactComponent as Facebook_SVG } from "../../assets/icon/facebook.svg";
import { ReactComponent as Messenger_SVG } from "../../assets/icon/messenger.svg";

import "../../assets/css/facebook.css";
import "../../assets/css/messenger.css";
import "../../assets/css/line.css";

export default class ProductDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: {},
      category: {},
    };

    this._isMounted = false;
    this._productId = 0;
  }

  componentDidMount() {
    this._isMounted = true;
    this._isMounted && this.getIdFromMatch();
    this._isMounted && this.getProduct();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  getIdFromMatch() {
    //console.log(this.props.match.params.id)
    if (this.props.match.params.id)
      this._productId = this.props.match.params.id;
  }

  getProduct() {
    axios
      .get(process.env.REACT_APP_API_URL + "/product/" + this._productId)
      .then((res) => {
        //console.log(res.data);
        this._isMounted &&
          this.setState({
            product: res.data,
          });
        this._isMounted && this.getProductCate();
      });
  }

  getProductCate() {
    const { product } = this.state;
    console.log(product);
    axios
      .get(process.env.REACT_APP_API_URL + "/category/" + product.category_id)
      .then((res) => {
        console.log(res.data);
        this._isMounted &&
          this.setState({
            category: res.data,
          });
      });
  }

  render() {
    const { product, category } = this.state;

    return product ? (
      <div className="regular-th" style={{ backgroundColor: "#f5f5f5" }}>
        <br />
        <Container>
          <p className="light-th pl-4" style={{ fontSize: "14px" }}>
            <Link to="/" style={{ color: "black" }}>
              e-Salesman
            </Link>{" "}
            &nbsp;>&nbsp; {category.category_name} &nbsp;>&nbsp;{" "}
            {product.product_name}
          </p>
        </Container>

        <Container>
          <div style={{ backgroundColor: "#ffffff" }}>
            <Row>
              <Col md="6">
                <div
                  className="ml-4 mt-5"
                  style={{
                    width: "100%",
                    height: "400px",
                    backgroundColor: "#ffffff",
                    backgroundImage: "url(" + product.product_image + ")",
                    backgroundSize: "contain",
                    backgroundRepeat: "no-repeat",
                    backgroundPosition: "center",
                  }}
                ></div>
              </Col>
              <Col md="6">
                <div className="mt-4">
                  <h4>{product.product_name}</h4>
                  <p className="light-th">| 0 {product.product_unit} ขายแล้ว</p>
                  <p style={{ fontSize: "40px", color: "#d93731" }}>
                    {" "}
                    <b>฿{product.product_price}</b>
                  </p>
                  <br />
                  <Row>
                    <Col md="3">
                      <p className="light-th" style={{ fontSize: "13px" }}>
                        รายละเอียด
                      </p>
                    </Col>
                    <Col md="7">
                      <p style={{ fontSize: "15px" }}>
                        {product.product_detail}
                      </p>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <p className="light-th" style={{ fontSize: "13px" }}>
                        มีสินค้าทั้งหมด
                      </p>
                    </Col>
                    <Col md="7">
                      <p style={{ fontSize: "15px" }}>
                        {product.product_inventory} {product.product_unit}
                      </p>
                    </Col>
                  </Row>
                  <br />
                  {/* facebook share button */}
                  <div id="fb-share-button" className="text-center" style={{width:'130px'}}>
                    &nbsp;&nbsp;
                    <Facebook_SVG />
                    <span> Share</span>&nbsp;&nbsp;
                  </div>
                  &nbsp;&nbsp;
                  {/* messenger share button */}
                  <div id="ms-share-button" className="text-center" style={{width:'135px'}}>
                    &nbsp;&nbsp;
                    <Messenger_SVG />
                    <span> Message</span>&nbsp;&nbsp;
                  </div>
                  &nbsp;&nbsp;
                  {/* line share button */}
                  <div id="ln-share-button" className="text-center" style={{width:'135px'}}>
                    &nbsp;&nbsp;
                    <Line_SVG />
                    <span> LINE it!</span>&nbsp;&nbsp;
                  </div>
                </div>
              </Col>
            </Row>
            <br />
          </div>
        </Container>
      </div>
    ) : (
      <>{/* skeleton from react-loading-skeleton */}</>
    );
  }
}
