import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  CardSubtitle,
} from "reactstrap";

export default class ProductDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: null,
    };

    this._isMounted = false;
    this._productId = 0;
  }

  componentDidMount() {
    this._isMounted = true;
    this._isMounted && this.getIdFromMatch();
    this._isMounted && this.getAProduct();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  getIdFromMatch() {
    //console.log(this.props.match.params.id)
    if (this.props.match.params.id)
      this._productId = this.props.match.params.id;
  }

  getAProduct() {
    axios
      .get(process.env.REACT_APP_API_URL + "/product/" + this._productId)
      .then((res) => {
        console.log(res.data);
        this._isMounted &&
          this.setState({
            product: res.data[0],
          });
      });
  }

  render() {
    const { product } = this.state;
    return (
      <div style={{ backgroundColor: "#f5f5f5" }}>
        <br />
        <Container>
          <div style={{ backgroundColor: "#ffffff" }}>
            <Row>
              <Col md="6">
                <div
                  className="ml-4 mt-5"
                  style={{
                    height: "400px",
                    backgroundColor: "#d9d9d9",
                    backgroundImage: "url(" + product.product_image + ")",
                    backgroundSize: "100%",
                    backgroundRepeat: "no-repeat",
                  }}
                ></div>
              </Col>
              <Col md="6"></Col>
            </Row>
            <br />
          </div>
        </Container>
      </div>
    );
  }
}
