import React, { Component } from "react";
import axios from "axios";

export default class ProductDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: null,
    };

    this._isMounted = false;
    this._productId = 0;
  }

  componentDidMount() {
    this._isMounted = true;
    this._isMounted && this.getIdFromMatch();
    this._isMounted && this.getAProduct();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  getIdFromMatch() {
    //console.log(this.props.match.params.id)
    if (this.props.match.params.id)
      this._productId = this.props.match.params.id;
  }

  getAProduct() {
    axios
      .get(process.env.REACT_APP_API_URL + "/product/" + this._productId)
      .then((res) => {
        console.log(res.data.product_id);
        this._isMounted &&
          this.setState({
            product: res.data,
          });
      });
  }

  render() {
    return (
      <div style={{ backgroundColor: "#f5f5f5" }}>
        <div style={{ backgroundColor: "#ffffff" }}></div>
      </div>
    );
  }
}
