import React, { Component } from "react";
import axios from "axios";
import { Container, Row, Col } from "reactstrap";

export default class ProductDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: null,
    };

    this._isMounted = false;
    this._productId = 0;
  }

  componentDidMount() {
    this._isMounted = true;
    this._isMounted && this.getIdFromMatch();
    this._isMounted && this.getProduct();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  getIdFromMatch() {
    //console.log(this.props.match.params.id)
    if (this.props.match.params.id)
      this._productId = this.props.match.params.id;
  }

  getProduct() {
    axios
      .get(process.env.REACT_APP_API_URL + "/product/" + this._productId)
      .then((res) => {
        console.log(res.data);
        this._isMounted &&
          this.setState({
            product: res.data,
          });
      });
  }

  getProductCate(){
    axios
    .get(process.env.REACT_APP_API_URL + "/product/" + this._productId)
    .then((res) => {
      console.log(res.data);
      this._isMounted &&
        this.setState({
          product: res.data,
        });
    });
  }

  render() {
    const { product } = this.state;

    return product ? (
      <div className="regular-th" style={{ backgroundColor: "#f5f5f5" }}>
        <br />
        <Container>
            e-Salesman > {}
        </Container>
        <Container>
          <div style={{ backgroundColor: "#ffffff" }}>
            <Row>
              <Col md="6">
                <div
                  className="ml-4 mt-5"
                  style={{
                    width: "100%",
                    height: "400px",
                    backgroundColor: "#ffffff",
                    backgroundImage: "url(" + product.product_image + ")",
                    backgroundSize: "contain",
                    backgroundRepeat: "no-repeat",
                    backgroundPosition: "center",
                  }}
                ></div>
              </Col>
              <Col md="6">
                <div className="mt-4">
                  <h4>{product.product_name}</h4>
                  <p className="light-th">| 0 {product.product_unit} ขายแล้ว</p>
                  <p style={{ fontSize: "40px", color: "#d93731" }}>
                    {" "}
                    <b>฿{product.product_price}</b>
                  </p>
                  <br />
                  <Row>
                    <Col md="3">
                      <p className="light-th" style={{ fontSize: "13px" }}>
                        รายละเอียด
                      </p>
                    </Col>
                    <Col md="7">
                      <p style={{ fontSize: "15px" }}>
                        {product.product_detail}
                      </p>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <p className="light-th" style={{ fontSize: "13px" }}>
                        มีสินค้าทั้งหมด
                      </p>
                    </Col>
                    <Col md="7">
                      <p style={{ fontSize: "15px" }}>
                        {product.product_inventory} {product.product_unit}
                      </p>
                    </Col>
                  </Row>
                </div>
              </Col>
            </Row>
            <br />
          </div>
        </Container>
      </div>
    ) : (
      <>{/* skeleton from react-loading-skeleton */}</>
    );
  }
}
